/**
 * Enables the modification of the Button appearance.
 *
 * The available styling options are:
 * - `default`
 * - `flat`
 * - `bare`
 * - `outline`
 *
 * @example
 * ```ts-no-run
 * <button kendo-button [look]="'bare'"></button>
 * ```
 */
export declare type ButtonLook = 'default' | 'flat' | 'bare' | 'outline';
